INSERT INTO items (id, guid, title, url, content, fetch_timestamp, publish_timestamp, channel_id) VALUES (68, 'https://www.lemonde.fr/series-d-ete/article/2023/07/30/donjons-dragons-face-a-une-double-menace-existentielle_6183914_3451060.html', 'Donjons & Dragons face à une double menace existentielle', 'https://www.lemonde.fr/series-d-ete/article/2023/07/30/donjons-dragons-face-a-une-double-menace-existentielle_6183914_3451060.html', '« Donjons & Dragons, la saga d’un jeu » (6/6). Inventé en 1974, le premier jeu de rôle affronte deux dangers en 2023 : la remise en cause de son modèle économique inspiré du logiciel libre et la critique virulente de ses biais culturels.', '2023-07-30 22:45:28.233880 +00:00', '2023-07-30 17:00:13.000000 +00:00', 2);
INSERT INTO items (id, guid, title, url, content, fetch_timestamp, publish_timestamp, channel_id) VALUES (69, 'https://www.lemonde.fr/sport/article/2023/07/30/demi-vollering-remporte-le-tour-de-france-femmes-2023-titre-majeur-d-une-saison-exceptionnelle_6183915_3242.html', 'Demi Vollering remporte le Tour de France Femmes 2023, titre majeur d’une saison exceptionnelle', 'https://www.lemonde.fr/sport/article/2023/07/30/demi-vollering-remporte-le-tour-de-france-femmes-2023-titre-majeur-d-une-saison-exceptionnelle_6183915_3242.html', 'Pour la première fois de sa carrière, la Néerlandaise de 26 ans a remporté la Grande Boucle, dimanche, à Pau. la Suissesse Marlen Reusser s’est imposée lors de la dernière étape, un contre-la-montre de 22 kilomètres.', '2023-07-30 22:45:28.233883 +00:00', '2023-07-30 17:06:47.000000 +00:00', 2);
