INSERT INTO items (id, guid, title, url, content, fetch_timestamp, publish_timestamp, channel_id) VALUES (74, 'https://www.lemonde.fr/politique/article/2023/07/30/reforme-des-retraites-publication-des-decrets-actant-la-fin-des-principaux-regimes-speciaux_6183888_823448.html', 'Réforme des retraites : les décrets actant la fin des principaux régimes spéciaux ont été publiés', 'https://www.lemonde.fr/politique/article/2023/07/30/reforme-des-retraites-publication-des-decrets-actant-la-fin-des-principaux-regimes-speciaux_6183888_823448.html', 'La suppression des régimes spéciaux sera effective à partir de septembre pour tous les nouveaux agents de la RATP, des industries électriques et gazières, des clercs de notaire et de la Banque de France. Les agents recrutés avant cette date continuent de bénéficier de ces régimes, mais ils n’échappent pas au report de l’âge de départ.', '2023-07-30 22:45:28.233890 +00:00', '2023-07-30 08:54:02.000000 +00:00', 2);
